})
export class FilterPipe implements PipeTransform {
  transform<T extends object>(values: T[], predicate: Partial<T>): T[] {
    return values.filter(val => isMatch(val, predicate));
  }
}

import { Pipe, PipeTransform } from '@angular/core';
import { find } from 'lodash-es';
import { Observable, map } from 'rxjs';

@Pipe({
  name: 'find',
  standalone: true
})
export class FindPipe implements PipeTransform {

  transform<T>(value: Observable<T[]>, predicate: Partial<T>) {
    return value.pipe(
      map(vals => find(vals, predicate) as T)
    );
  }

}

import { Pipe, PipeTransform } from "@angular/core";
import Fuse from 'fuse.js'
import { Observable, map } from "rxjs";

export interface FuseOptions<T> extends Omit<Fuse.IFuseOptions<T>, "keys"> {
  search: string | null | undefined,
  keys?: keyof T | Array<keyof T>
}

@Pipe({
  name: "fuse",
  standalone: true
})
export class FusePipe implements PipeTransform {
  transform<T>(values: T[], options: FuseOptions<T>): T[] 
  transform<T>(values: Observable<T[]>, options: FuseOptions<T>): Observable<T[]>
  transform<T>(values: Observable<T[]> | T[], options: FuseOptions<T>): Observable<T[]> | T[] {
    if (options.search == null || options.search.length === 0)
      return values;
    
    return values instanceof Observable
      ? values.pipe(
        map(vals => new Fuse(vals, { 
          threshold: .1,
          ignoreLocation: true,
          ...options as Fuse.IFuseOptions<T>
        })
        .search(options.search!)
        .map(val => val.item))
      )
      : new Fuse(values, { 
          threshold: .1,
          ignoreLocation: true,
          ...options as Fuse.IFuseOptions<T>
        })
        .search(options.search)
        .map(val => val.item);
  }
}

import { Pipe, PipeTransform } from '@angular/core';
import { mapValues } from 'lodash-es';

@Pipe({
  name: 'toStringValues',
  standalone: true
})
export class ToStringValuesPipe implements PipeTransform {

  transform<T extends object>(values: T[]) {
    return values?.map(obj => mapValues(obj, val => val + ""));
  }

}

import { Pipe, PipeTransform } from "@angular/core";

@Pipe({
  name: 'utcDate',
  standalone: true
})
export class UtcDatePipe implements PipeTransform {
  transform(value: Date) {
    return value.toFormat("yyyy-MM-dd")
  }
}

import { Pipe, PipeTransform } from "@angular/core";

