import { Directive } from "@angular/core";
import { flow } from "lodash-es";
import { withNoop } from "./with-noop";
import { AnyFunction } from "./types";

@Directive()
class ReactiveDirective {}

export function AngularDirective<T1 extends AnyFunction>(h1: T1): ReturnType<T1>
export function AngularDirective<T1 extends AnyFunction, T2 extends AnyFunction>(h1: T1, h2: T2): ReturnType<T1> & ReturnType<T2>
export function AngularDirective<T1 extends AnyFunction, T2 extends AnyFunction, T3 extends AnyFunction>(h1: T1, h2: T2, h3: T3): ReturnType<T1> & ReturnType<T2> & ReturnType<T3>
export function AngularDirective<T1 extends AnyFunction, T2 extends AnyFunction, T3 extends AnyFunction, T4 extends AnyFunction>(h1: T1, h2: T2, h3: T3, h4: T4): ReturnType<T1> & ReturnType<T2> & ReturnType<T3> & ReturnType<T4>
export function AngularDirective<T1 extends AnyFunction, T2 extends AnyFunction, T3 extends AnyFunction, T4 extends AnyFunction, T5 extends AnyFunction>(h1: T1, h2: T2, h3: T3, h4: T4, h5: T5): ReturnType<T1> & ReturnType<T2> & ReturnType<T3> & ReturnType<T4> & ReturnType<T5>
export function AngularDirective<T1 extends AnyFunction, T2 extends AnyFunction, T3 extends AnyFunction, T4 extends AnyFunction, T5 extends AnyFunction, T6 extends AnyFunction>(h1: T1, h2: T2, h3: T3, h4: T4, h5: T5, h6: T6): ReturnType<T1> & ReturnType<T2> & ReturnType<T3> & ReturnType<T4> & ReturnType<T5> & ReturnType<T6>
export function AngularDirective(): ReturnType<typeof withNoop> & ReactiveDirective;
export function AngularDirective(...args: AnyFunction[]): any {
  if (args == null || args.length === 0) return ReactiveDirective;
  
  const withHooks = flow(args);
  return withHooks(ReactiveDirective);
}

import { ChangeDetectionStrategy, Component, Directive, EventEmitter, Input, Output, ViewChild } from "@angular/core";
import { debounceTime, map, merge } from "rxjs";
import SignaturePad, { PointGroup } from "signature_pad";
import { AngularComponent, withAfterViewInit } from "../../lifecycles";
import { PushPipe } from "@rx-angular/template/push";
import { use } from "../../rxjs/use";
import { useElement, useParentElement } from "../../angular/element";

@Directive({
  selector: "canvas[signature-pad]",
  standalone: true
})
export class SignaturePadDirective {
  element = useElement<HTMLCanvasElement>();

  signaturePad = new SignaturePad(this.element, {
    minWidth: 2,
    maxWidth: 5,
    backgroundColor: "rgb(255,255,255)"
  });

  @Input()
  points?: PointGroup[] = [];

  @Output()
  pointsChange = new EventEmitter<PointGroup[]>();

  ngOnInit() {
    this.signaturePad.fromData(this.points || []);
  }

  dataPoints() {
    return this.signaturePad.toData();
  }

  toDataUrl() {
    return this.signaturePad.toDataURL();
  }

  clear() {
    this.signaturePad.clear();
  }
}

@Component({
  selector: "signature-pad",
  template: `
    <canvas 
      signature-pad 
      [points]="points"
      class="border border-black bg-white" 
      (window:resize)="resize.next()"
      [width]="width$ | push"
      [height]="height$ | push">
    </canvas>
  `,
  standalone: true,
  changeDetection: ChangeDetectionStrategy.OnPush,
  imports: [PushPipe, SignaturePadDirective]
})
export class SignaturePadComponent extends AngularComponent(withAfterViewInit) {
  @ViewChild(SignaturePadDirective)
  signatureDirective?: SignaturePadDirective;

  @Input()
  points?: PointGroup[] = [];

  parentElement = useParentElement();
  resize = use();

  width$ = merge(this.afterViewInit(), this.resize()).pipe(
    debounceTime(300),
    map(() => this.parentElement.offsetWidth - 2)
  );

  height$ = merge(this.afterViewInit(), this.resize()).pipe(
    debounceTime(300),
    map(() => this.parentElement.offsetHeight - 2)
  );

  dataPoints() {
    return this.signatureDirective?.dataPoints();
  }

  toDataUrl() {
    return this.signatureDirective?.toDataUrl();
  }

  clear() {
    this.signatureDirective?.clear();
  }
}

import { Directive, EventEmitter, HostListener, Output } from "@angular/core";
import { Camera, CameraResultType, Photo } from "@capacitor/camera";

@Directive({
  selector: "[takePhoto]",
  standalone: true
})
export class TakePhotoDirective {
  @Output("takePhoto")
  takePhoto = new EventEmitter<Photo>();

  @HostListener("click")
  async take() {
    const photo = await Camera.getPhoto({
      resultType: CameraResultType.Base64
    });
    this.takePhoto.emit(photo);
  }
}

import { Directive, EventEmitter, HostListener, Output } from "@angular/core";
import { Camera, CameraResultType, CameraSource, Photo } from "@capacitor/camera";

@Directive({
  selector: "[uploadPhoto]",
  standalone: true
})
export class UploadPhotoDirective {
  @Output("uploadPhoto")
  uploadPhoto = new EventEmitter<Photo>();

  @HostListener("click")
  async upload() {
    const photo = await Camera.getPhoto({
      resultType: CameraResultType.Base64,
      source: CameraSource.Photos
    });

    this.uploadPhoto.emit(photo);
  }
}

