@Component({
  selector: "asset-info-page",
  template: `
    <ion-header>
      <ion-toolbar>
        <ion-title class="ion-text-center">Asset Info</ion-title>
      </ion-toolbar>
    </ion-header>

    <ion-content class="ion-padding">
      <ion-card *rxIf="assetInfo$; let info">
