        </ion-item>
      </rx-virtual-scroll-viewport>
    </ion-list>

    <ion-modal #modal [trigger]="id">
      <ng-template>
        <ion-header>
          <ion-toolbar>
            <ion-title class="text-center">Select Responses</ion-title>
            <ion-buttons slot="end">
              <ion-button (click)="modal.dismiss(); saved.next()">
                <ion-icon name="close-outline" slot="icon-only" />
              </ion-button>
            </ion-buttons>
          </ion-toolbar>
          <ion-toolbar>
            <ion-searchbar 
              [(ngModel)]="searchValue"
              debounce="300" 
              placeholder="Search" />
          </ion-toolbar>
        </ion-header>

        <ion-content>
          <ion-list class="h-full">
            <rx-virtual-scroll-viewport [itemSize]="50">
              <checkbox-question 
                *rxVirtualFor="let question of section.Questions
                  | fuse: {
                    search: searchValue,
                    keys: ['QuestionText']
                  }" 
                class="w-full"
                [question]="question" />
            </rx-virtual-scroll-viewport>
          </ion-list>
        </ion-content>
      </ng-template>
